INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (4, 'leadership', 'about', 6, 'light', 'white', null, null, 'white', null, '<div class="text-center mt-3 mb-3 mt-md-5 h7" style="font-size: 9vw;"> Leadership</div><div id="leadership" class="container card-columns"> <div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/BenStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>BEN TUGWELL</strong></h6> <p class="card-text"> Elder | Preaching & Vision <br>Email: <a class="text-info" href="mailto:ben@livintegrity.org">ben@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/KirkStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>KIRK BURTCH</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:kirk@livintegrity.org">kirk@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/HalStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>HAL HOLLOMAN</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:hal@livintegrity.org">hal@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/HarrisStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>JOSH HARRIS</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:josh.harris@livintegrity.org">josh.harris@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/AdamStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>ADAM MOTTER</strong></h6> <p class="card-text"> Deacon | Production & Facilities <br>Email: <a class="text-info" href="mailto:cpman@yahoo.com">cpman@yahoo.com</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/AubreyStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>AUBREY MAUPIN</strong></h6> <p class="card-text"> Administrative Assistant <br>Email: <a class="text-info" href="mailto:aubrey@livintegrity.org">aubrey@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/JamieStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>JAMIE PECKOUS</strong></h6> <p class="card-text"> Director | Integrity Kids <br>Email: <a class="text-info" href="mailto:jamie@livintegrity.org">jamie@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/JonStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>JON PARISH</strong></h6> <p class="card-text"> Director | Operations <br>Email: <a class="text-info" href="mailto:jon@livintegrity.org">jon@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/JoshStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>JOSH MYERS</strong></h6> <p class="card-text"> Executive Director <br>Email: <a class="text-info" href="mailto:josh@livintegrity.org">josh@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/BlankStaff.jpeg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>PHILLIP WALL</strong></h6> <p class="card-text"> Director | Worship <br>Email: <a class="text-info" href="mailto:phillip@livintegrity.org">phillip@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/StephenStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>STEPHEN PECKOUS</strong></h6> <p class="card-text"> Deacon | Member Care <br>Email: <a class="text-info" href="mailto:stephenpeckous@gmail.com">stephenpeckous@gmail.com</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/RayStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>RAY SHIMER</strong></h6> <p class="card-text"> Director | Finance <br>Email: <a class="text-info" href="mailto:rshimer@suddenlink.net">rshimer@suddenlink.net</a> </p></div></div></div>', null, 1, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (5, 'contact us', 'about', 7, 'light', 'dark', null, null, 'white', null, '<div id="contact" class="container bg-light"> <div class="display-4 text-center mb-5 h7">Contact</div><div class="row mb-5"> <div class="col-6 col-lg-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Address</div><div class="mb-3 text-center">2725A&nbsp;E.&nbsp;14th&nbsp;St. Greenville,&nbsp;NC&nbsp;27858</div></div><div class="col-6 col-lg-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Office Hours</div><div class="mb-3 text-center">9:00am&nbsp;-&nbsp;5:00pm Mon&nbsp;-&nbsp;Thu&nbsp;(EST)</div></div><div class="col-6 col-lg-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Email</div><div class="mb-3 text-center"><a href="mailto:info@liveintegrity.org?subject=Hi" class="badge badge-info p-2" style="border-radius: 2px;">info@liveintegrity.org</a></div></div><div class="col-6 col-lg-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Phone</div><div class="mb-3 text-center"><a href="tel:+12527564508" class="badge badge-info p-2" style="border-radius: 2px;">1(252)756-4508</a></div></div></div><div class="embed-responsive embed-responsive-1by1 mx-auto" style="max-width: 600px"> <iframe class="embed-responsive-item" style="" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2864.3161577080405!2d-77.3445332202819!3d35.579932783701764!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89aedd71b55fe63f%3A0xcbc3538a485074ed!2s2725+E+14th+St%2C+Greenville%2C+NC+27858!5e0!3m2!1sen!2sus!4v1470882936894" frameborder="0" allowfullscreen="allowfullscreen"></iframe> </div></div>', null, 0, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (6, 'sermons', 'resources', 8, 'dark', 'white', 3, '<div style="text-alignment: center"><span>We do sermons most days!</span</div>', 'white', null, null, null, 1, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (7, 'mission', 'about', 16, 'dark', 'white', null, null, 'white', null, '<div class="text-center mt-3 mb-3 mt-md-5 h7" style="font-size: 9vw;"> MISSION</div><div class="container"> <blockquote class="blockquote text-center"> <em>INTEGRITY CHURCH EXISTS TO MATURE AND MULTIPLY BELIEVERS TO LEAVE A GOSPEL LEGACY.</em> </blockquote> <h1 class="mb-3">Maturing...</h1> <h6><strong>CHURCH GATHERED</strong></h6> <p class="text-justify"> God alone is worthy of all our affection and worship. For the believer, all of life is worship. Truly, every thought, word and deed ought to show love for the one true God. The believers life of worship is nurtured in the context of the weekly corporate gathering of believers. As a church we gather to worship, fellowship and hear God’s Word proclaimed. It is our conviction to mainly preach through books of the Bible and make Jesus central in all we say and do. We believe that responding corporately to the gospel causes us to mature and conform to the image of Christ. <span class="text-secondary">1 Corinthians 10:31; Col. 3:1-17; Eph. 2:22; 1 Corinthians 3:16-17; Romans 12:1-2</span> </p><h6><strong>CHURCH SCATTERED</strong></h6> <p class="text-justify border-bottom"> We worship a triune God who has eternally existed as the Father, the Son and the Holy Spirit. In identifying God as a trinity, we recognize that God is communal. We are made in God’s image and are therefore designed for a relationship with God and one another. We are not intended to live isolated lives. Therefore, as a church we scatter throughout the city for Gospel-Centered Community. This is primarily expressed in our Life Groups which meet throughout the week to fellowship with one another, discuss the sermons, pray together and serve our city. <span class="text-secondary">Acts 2:42-47, Hebrews 3:12-13, 1 Corinthians 12:12-31</span><br><br></p><h1 class="mb-3">Multiplying...</h1> <h6><strong>MISSIONAL LIVING</strong></h6> <p class="text-justify"> Jesus Christ is the ultimate example of humble service to others. In John 13:1-20, we read about Jesus washing the disciples’ feet. This was not the typical rabbinical task but was reserved to the lowest of servants. Christ ultimately serves us through his death, burial and resurrection for the forgiveness of our sins. Missional Living is a lifestyle that flows out of a heart captivated by the truth of the Gospel. God has shown us love and grace and we reflect the gospel through service to one another. Missional living is pursued in various ways based on how God has gifted us as individuals. Whether it’s greeting on the hospitality team, serving Integrity Kids, discipling others or opening their homes to their neighbors, believers have countless ways to live missionally. </p><h6><strong>PLANTING CHURCHES</strong></h6> <p class="text-justify"> In one of the most famous passages on discipleship, Matthew 28:18-20, Jesus issues the Great Commission for the church. Christ commands us to go into all the world and to multiply ourselves through showing love for others by teaching them his commands. As a church, we fulfill the Great Commission through multiplying our efforts by planting solid biblical churches who proclaim the gospel. We are called to multiply through planting churches to the glory of God. <span class="text-secondary">Matthew 28:18-20; 2 Corinthians 5:11-12</span> </p></div>', null, 1, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (8, 'integrity kids', 'about', 17, 'dark', 'white', 6, null, 'white', null, '<div class="container-fluid text-center d-lg-none"> <a href="#curriculum" class="badge badge-info mt-2 mr-1" style="border-radius: 2px; font-size: 115%"> Curriculum </a> <a href="#tgp" class="badge badge-info mt-2 mx-1" style="border-radius: 2px; font-size: 115%"> The Gospel Project </a> <a href="#resources" class="badge badge-info mt-2 ml-1" style="border-radius: 2px; font-size: 115%"> Family Resources </a></div><div class="container mt-5"> <h4><strong>SUNDAY MORNINGS</strong></h4> <p class="text-justify"> On Sunday mornings we have 4 separate classrooms for infants, toddlers, preschoolers and elementary students through the 2nd grade. </p><div class="container"><hr style="border-color: white"/> </div><h5> Nursery | <strong>6&nbsp;weeks&nbsp;-&nbsp;17&nbsp;months <span style="color: orange; text-shadow: 1px 1px 0 #000;">(Orange&nbsp;Room)</span> </strong> </h5> <p class="ml-3 text-justify"> Our goal for this age is to provide a loving, safe and fun environment for infants. The same core volunteers serve in this classroom each week to provide consistency for parents and infants. You will find developmentally appropriate toys and books to keep your infant happy while you are worshiping. There is a communication board for parents to leave instructions for volunteers. We think it is important for infants to receive a lot of individual attention and care. The teacher to infant ratio in this class is 2:1. </p><h5> Toddlers | <strong>18&nbsp;months&nbsp;-&nbsp;35&nbsp;months <span style="color: blue; text-shadow: 1px 1px 0 #000;">(Blue&nbsp;Room)</span> </strong> </h5> <p class="ml-3 text-justify"> Our goal for this age is to expose toddlers to the biblical truth of scriptures through story telling, singing, scripture memory, crafts, &amp; interactive activities. Toddlers also have playtime and snack time on Sunday mornings. &nbsp;This classroom is working through <em>The Gospel Project</em> curriculum. The child to teacher ratio is 4:1. </p><h5> Preschool | <strong>3&nbsp;-&nbsp;4&nbsp;years&nbsp; <span style="color: green; text-shadow: 1px 1px 0 #000;">(Green&nbsp;Room)</span> </strong> </h5> <p class="ml-3 text-justify"> In the preschool class, children continue to be exposed to the teaching of scriptures and are assisted with making gospel connections. Teachers in this classroom make Bible stories come alive through story telling, scripture memory, interactive activities and crafts. Preschoolers are provided with an activity sheet each week that helps to reiterate the lesson and can be used as a tool to review lessons at home. Preschoolers will also have snacktime and playtime each Sunday. The child to teacher ratio is 5:1. </p><h5> Elementary | <strong>K&nbsp;-&nbsp;2nd&nbsp;Grade <span style="color: yellow; text-shadow: 1px 1px 0 #000;">(Yellow&nbsp;Room)</span> </strong> </h5> <p class="ml-3 text-justify"> Students continue to work through <em>The Gospel Project</em>&nbsp;curriculum in the Elementary classroom. Each Sunday students will be engaged with a Bible story, music, scripture memory, crafts and interactive activities. Each student &nbsp;will be given an activity page to help emphasize the truths they have learned and to aid parents with at home discussion. The student to teacher ratio in this classroom is 6:1. </p><div class="container"><hr style="border-color: white"/></div><h4 id="curriculum" class="mt-5"><b>CURRICULUM</b></h4> <p style="text-align: justify;"> The Toddler, Preschool and Elementary classrooms are using <em>The Gospel Project</em> curriculum. This curriculum works chronologically through the Bible from Genesis to Revelation over the course of three years. We believe it is important to partner with parents in exposing children to all the stories of the Bible in an organized manner. Each child is provided with an activity page and take-home resource to enable parents in guiding and reviewing lessons taught each Sunday. </p><p style="text-align: justify;"> <em>The Gospel Project</em> is formatted by age group allowing each classroom to learn at their appropriate developmental pace. Students have the opportunity to hear the Gospel message taught, not as a collection of disjointed stories, but as the unified story of God’s redemptive plan for His children. </p><div id="tgp" class="lead">THE GOSPEL PROJECT INFORMATION</div><p> <a href="https://www.gospelproject.com/kids/preschool/core-values-study-plan/" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">Mission</a> <a href="https://www.gospelproject.com/kids/preschool/core-values-study-plan/#core-values" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">Core Values</a> <a href="https://www.gospelproject.com/kids/preschool/core-values-study-plan/#study-plan" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">Study Plan</a> </p><div id="resources" class="lead">FAMILY RESOURCES</div><p> <a href="http://www.sallylloyd-jones.com/books/jesus-storybook-bible/" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">Jesus Storybook Bible</a> <a href="http://www.shepherdpress.com/product/shepherding-a-childs-heart/" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">Shepherding a Child''s Heart</a> <a href="https://www.gospelproject.com/2014/10/08/big-picture-bible-series-kids/" class="badge badge-info p-2 m-2" style="border-radius: 2px; font-size: 115%" target="_blank" rel="noopener">The Big Picture Bible Series</a> </p></div>', null, 1, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (9, 'membership', 'connect', 18, 'light', 'dark', null, null, 'white', null, '<div class="text-center mt-3 mb-3 mt-md-5 h7" style="font-size: 9vw;"> MEMBERSHIP</div><div class="container" id="membership"> <h4><strong>WHAT IS CHURCH MEMBERSHIP?</strong></h4> <p class="text-justify">Church Membership is more than consistent attendance. The church is the vehicle by which God accomplishes His purposes in the world. God invites followers of Jesus into his purpose for the world through involvement in the local church. Members partner together to fulfill God’s purposes and hold one another to this high calling.</p><p class="text-justify">We hope that you make a decision to be part of our church family.</p><h4><strong>MEMBERSHIP CLASS</strong></h4> <p>We want you to make an informed decision about joining Integrity Church. By providing a class, we can cover what it means to be a Member and our beliefs, mission, vision and values.</p><h4><strong>BAPTISM</strong></h4> <p class="text-justify"> If you have not been baptized following your conversion, we ask that you take this step before becoming a member. If you have not been baptized but would like to, please email <a class="text-info" href="mailto:aubrey@liveintegrity.org">aubrey@liveintegrity.org</a>. </p><h4><strong>SIGNING THE AGREEMENT FORM</strong></h4> <p class="text-justify"> The final step in becoming a member is to meet with your Small Group leader or other leader and then sign the Membership Agreement. We will explain member expectations, listen to your testimony and answer any questions you might have. Once you fulfill the requirements of membership and sign the commitment, you are considered a member. </p></div>', null, 1, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (10, 'giving', 'connect', 14, 'light', 'dark', null, null, 'light', null, '<div class="text-center mt-3 mb-3 mt-md-5 h7" style="font-size: 9vw;">GIVING</div><div class="container" id="giving"> <h4><strong>WHY GIVE?</strong></h4> <p class="text-justify">As a church, we have a tremendous opportunity to be generous. In fact, giving cheerfully and sacrificially of our finances is part of our worship as believers. Your generosity supports Integrity’s mission to mature and multiply disciples of Jesus.</p><h4><strong>ONLINE GIVING</strong></h4> <p>To make giving to Integrity as accessible as possible, we provide online giving. You can give a one-time donation, and you also have the ability to set up a recurring donation which is very helpful to the church.</p><p>We process online transactions via Planning Center, which is our software platform that helps streamline the administration of Integrity Church. By clicking the link below, you can give online, set up recurring giving, and designate gifts to specific causes. If you need to adjust your gift or have any questions, please email <a class="text-info" href="mailto:info@liveintegrity.org">info@liveintegrity.org</a>.</p><a class="btn btn-info btn-lg btn-block" href="https://integritychurch.churchcenteronline.com/giving">CLICK HERE TO GIVE ONLINE</a> <div class="embed-responsive embed-responsive-16by9 my-3"> <iframe class="embed-responsive-item" width="425" height="240" src="//player.vimeo.com/video/158101975?title=0&amp;amp;byline=0"></iframe> </div><h4><strong>CASH & CHECKS</strong></h4> <p class="text-justify"> Please make checks payable to Integrity Church. These may be dropped in the offering boxes during the service or in person at the church office. You can also mail checks to the address below: </p><p class="text-center"> <strong> Integrity Church<br>2725A East 14th St.<br>Greenville, NC 27858 </strong> </p><h4><strong>DONATIONS & CONTRIBUTION</strong></h4> <p class="text-justify">If you made a financial contribution to Integrity Church, you may receive an updated report at any time by emailing us at <a class="text-info" href="mailto:info@liveintegrity.org">info@liveintegrity.org</a>. We also send out quarterly statements to everyone who has donated.</p><h4><strong>REQUEST A STATEMENT</strong></h4> <p class="text-justify">Members of Integrity Church may request a copy of our most recent budget or ask general questions about our financial practices at any time by contacting us at <a class="text-info" href="mailto:info@liveintegrity.org">info@liveintegrity.org</a>.</p></div>', null, 1, 1, '2018-05-02 21:45:58', '2018-05-02 21:46:02');
