INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (12, 'integrity stories', 'resources', 4, 'dark', 'white', null, null, 'white', null, null, null, 0, 1, null, null);
INSERT INTO integrity.pages (id, name, parent, link_id, bg_color, text_color, billboard_img_id, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active, created_at, updated_at) VALUES (13, 'small groups', 'connect', 2, 'dark', 'white', null, null, 'white', null, '<div class="text-center mt-3 mb-3 mt-md-5 h7" style="font-size: 9vw;"> SMALL GROUPS</div><div class="container" id="sg"> <h4><strong>WHY SHOULD I JOIN A SMALL GROUP?</strong></h4> <div style="text-align: justify;"> <p> We gather together to learn how to love God and love one another. Small groups are not only the primary entry point into community at Integrity, they also represent the church on a personal level. Joining a small group is the best way to truly be a part of Integrity. We have groups that meet all across the city most nights of the week. </p><p> At Integrity Church, we desire to see Christians grow more like Jesus. One way God accomplishes this is through authentic Christian community: living life together and challenging one another to grow as disciples of Christ. We desire to be a church where meaningful authentic relationships are formed and grow. </p></div><div class="text-right"><small class="float-md-right text-warning">* childcare provided</small></div><div> <h4><strong>MONDAY NIGHT | 6:30PM</strong></h4> <p> <strong>Peckous/Biddlecome Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/BMfJ91uLWyE2">2283 Shire Dr. Winterville, NC</a> </p><h4 class="mt-3"><strong>TUESDAY NIGHT | 6:30PM</strong></h4> <p> <strong>Brafford Group<br></strong> <a class="text-info" href="https://goo.gl/maps/qQPGxPZq25R2">353 Tradewinds Way Grimesland, NC</a> <br><strong>Marcoux/Kornegay Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/MYsTWMdRGjy">303 Granville Dr. Greenville, NC</a> <br><strong>Maw Group<br></strong> <a class="text-info" href="https://goo.gl/maps/wNLV83ANn252">123 Harell St. Greenville, NC</a> </p><h4 class="mt-3"><strong>WEDNESDAY NIGHT | 6:30PM</strong></h4> <p> <strong>Holloman Group*<br></strong> <a class="text-info" href="https://www.google.com/maps/dir//35.5624983,-77.3040588/@35.5616921,-77.3043563,372m/data=!3m1!1e3">903 Derbyshire Ln. Greenville, NC</a> <br><strong>Myers Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/8xvEzNnXgz32">206 Erith Ct. Greenville, NC</a> <br><strong>Parish Group<br></strong> <a class="text-info" href="https://goo.gl/maps/UXED7geoUhB2">4123 River Chase Dr. Greenville, NC</a> <br><strong>Steward Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/2DuqHTGzYH62">2805 Noah Ct. Greenville, NC</a> <br><strong>Stump Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/49Wf5z3W6kP2">4104 Hardwick Ct. Greenville, NC</a> <br><strong>Zimmerman Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/u8Z8TFeDRYm">2705 Camille Dr. Winterville, NC</a> </p><h4 class="mt-3"><strong>THURSDAY NIGHT | 6:30PM</strong></h4> <p><strong>Harris/Bicknell Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/QJZxwWNScfv">2906 Cresset Dr. Winterville, NC</a><br><strong>Sanderlin Group<br></strong> <a class="text-info" href="https://goo.gl/maps/tQfJgt3nMxG2">205 Greenwood Dr. Greenville, NC</a><br><strong>Shimer Group*<br></strong> <a class="text-info" href="https://goo.gl/maps/2jM2RfBhgy52">2164 Pleasure Mount Ln. Greenville, NC</a> </p></div><p>If you have any questions about Small Groups or would like help finding one please email <a class="text-info" href="mailto:info@liveintegrity.org">info@liveintegrity.org</a>. </p></div>', null, 1, 1, null, null);