INSERT INTO integrity.pages (id, name, link, active, parent, `desc`, billboard_text, billboard_img, included_media, content, basic) VALUES (5, 'contact us', 7, 1, 'about', null, null, null, null, '<div class="container"> <div class="display-4 text-center mb-5">Contact</div><div class="row mb-5"> <div class="col-6 col-xl-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Address</div><div class="mb-3 text-center">2725A&nbsp;E.&nbsp;14th&nbsp;St. Greenville,&nbsp;NC&nbsp;27858</div></div><div class="col-6 col-xl-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Office Hours</div><div class="mb-3 text-center">9:00am&nbsp;-&nbsp;5:00pm Mon&nbsp;-&nbsp;Thu&nbsp;(EST)</div></div><div class="col-6 col-xl-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Email</div><div class="mb-3 text-center"><a href="mailto:info@liveintegrity.org?subject=Hi" class="badge badge-primary p-2" style="border-radius: 2px; font-size: %;">info@liveintegrity.org</a></div></div><div class="col-6 col-xl-3 mb-3"> <div class="lead text-center text-uppercase border-bottom mb-3">Phone</div><div class="mb-3 text-center"><a href="tel:+12527564508" class="badge badge-primary p-2" style="border-radius: 2px; font-size: 115%">1(252)756-4508</a></div></div></div><div class="embed-responsive embed-responsive-1by1 mx-auto" style="max-width: 600px"> <iframe class="embed-responsive-item" style="" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2864.3161577080405!2d-77.3445332202819!3d35.579932783701764!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89aedd71b55fe63f%3A0xcbc3538a485074ed!2s2725+E+14th+St%2C+Greenville%2C+NC+27858!5e0!3m2!1sen!2sus!4v1470882936894" frameborder="0" allowfullscreen="allowfullscreen"></iframe> </div></div>', 0);
