INSERT INTO integrity.pages (id, name, parent, link, bg_color, text_color, billboard_img, billboard_text, billboard_text_color, `desc`, content, included_media, basic, active) VALUES (4, 'leadership', 'about', 6, 'dark', 'white', null, null, 'white', null, '<div class="display-4 text-center mt-3 mt-md-5 h7">Leadership</div><div id="leadership" class="container card-columns"> <div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/BenStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>BEN TUGWELL</strong></h6> <p class="card-text"> Elder | Preaching & Vision <br>Email: <a class="text-info" href="mailto:ben@livintegrity.org">ben@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/KirkStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>KIRK BURTCH</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:kirk@livintegrity.org">kirk@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/HalStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>HAL HOLLOMAN</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:hal@livintegrity.org">hal@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/HarrisStaff.jpg" alt="Lead Pastor"> <div class="card-body bg-dark p-1"> <h6 class="card-title"><strong>JOSH HARRIS</strong></h6> <p class="card-text"> Elder <br>Email: <a class="text-info" href="mailto:josh.harris@livintegrity.org">josh.harris@livintegrity.org</a> </p></div></div><div class="card"> <img class="card-img-top" src="http://localhost:8888/integrity/public/img/leadership/AdamStaff.